
####################################################

Function Get-IntuneApplication(){

<#
.SYNOPSIS
This function is used to get applications from the Graph API REST interface
.DESCRIPTION
The function connects to the Graph API Interface and gets any applications added
.EXAMPLE
Get-IntuneApplication
Returns any applications configured in Intune
.NOTES
NAME: Get-IntuneApplication
#>

[cmdletbinding()]

$graphApiVersion = "Beta"
$Resource = "deviceAppManagement/mobileApps"
    
    try {
        
    $uri = "https://graph.microsoft.com/$graphApiVersion/$($Resource)"
    (Invoke-RestMethod -Uri $uri -Headers $headers1b -Method Get).Value | ? { (!($_.'@odata.type').Contains("managed")) }

    }
    
    catch {

    $ex = $_.Exception
    Write-Host "Request to $Uri failed with HTTP Status $([int]$ex.Response.StatusCode) $($ex.Response.StatusDescription)" -f Red
    $errorResponse = $ex.Response.GetResponseStream()
    $reader = New-Object System.IO.StreamReader($errorResponse)
    $reader.BaseStream.Position = 0
    $reader.DiscardBufferedData()
    $responseBody = $reader.ReadToEnd();
    Write-Host "Response content:`n$responseBody" -f Red
    Write-Error "Request to $Uri failed with HTTP Status $($ex.Response.StatusCode) $($ex.Response.StatusDescription)"
    write-host
    break

    }

}



####################################################


####################################################
####7zip Icon
####################################################

$ApplicationName = "7zip"
$Application = Get-IntuneApplication | ? { $_.displayName -eq "$ApplicationName" }
$id = $application.id
$uri = "https://graph.microsoft.com/beta/deviceAppManagement/mobileApps/$id/"
Invoke-RestMethod -Uri $uri -Headers $headers1b -Method Get

$contentpart1 = '{"@odata.type":  "#microsoft.graph.win32LobApp","largeIcon":  {
                      "type":  "image/jpeg",
                      "value":  ""
                  }
}'

$content = $contentpart1
Invoke-RestMethod -Uri $uri -Headers $headers1b -Method patch -Body $content -ErrorAction Stop -ContentType 'application/json'


